#!/bin/bash
INSTALLERPATH=$PWD; sudo mkdir /opt/; cd /opt/; sudo dnf install npm; sudo npm install -g nativefier; mkdir WebApps; cd WebApps; nativefier --name "OneDrive" "https://www.onedrive.com/"; nativefier --name "Office" "https://www.office.com/"; nativefier --name "Google-Drive" "https://drive.google.com/"; nativefier --name "Google-Meet" "https://meet.google.com/"; nativefier --name "WhatsApp" "https://web.whatsapp.com/"; nativefier --name "Google-Tradutor" "https://translate.google.com"; nativefier --name "Google-Classroom" "https://classroom.google.com"; cd $INSTALLERPATH; sudo cp OneDrive.desktop /usr/share/applications/; sudo cp Google-Drive.desktop /usr/share/applications/; sudo cp Office.desktop /usr/share/applications/; sudo cp Google-Meet.desktop /usr/share/applications/; sudo cp WhatsApp.desktop /usr/share/applications/; sudo cp Google-Tradutor.desktop /usr/share/applications/; sudo cp Google-Classroom.desktop /usr/share/applications/; sudo cp ./Icons/Google-Classroom/icon.png /opt/WebApps/Google-Classroom-linux-x64/resources/app/; sudo cp ./Icons/Google-Tradutor/icon.png /opt/WebApps/Google-Tradutor-linux-x64/resources/app/; sudo cp ./Icons/Office/icon.png /opt/WebApps/Office-linux-x64/resources/app/; sudo cp ./Icons/OneDrive/icon.png /opt/WebApps/OneDrive-linux-x64/resources/app/; cd
